# -*- coding: utf-8 -*-import osimport randomimport timefrom spider.Ali1688 import Ali1688from utils.data_process import get_result_img_namefrom configs.config import log"""程序启动入口"""# 目前1688搜索入口不用登录也可以搜索，把登录掉了。另外一个文件是单独登录的启动if __name__ == '__main__':    dir = os.getcwd()    imgPath = dir + '\\' + 'data\\img'    imgList = os.listdir(imgPath)    img_name_exist = get_result_img_name()    log.info('已爬取的图片 {}'.format(img_name_exist))    # print(img_name_exist)    for img in imgList:        if img not in img_name_exist:            imagePath = dir + '\\data\\img\\' + img            log.info('*************正在采集图片： {} 的搜索数据*************'.format(img))            result = Ali1688(imagePath).run()            time.sleep(random.uniform(30,40))            # print(result)        else:            log.info('{} 今天已经爬取过了'.format(img))    log.info('*************全部图片的搜索已经完成！*************')